/* eslint-disable react/no-unescaped-entities */
import "./contact.css"
import Phone from "../../img/phone.jpg"
import Email from "../../img/email.jpg"
import Address from "../../img/address.jpg"
import React, { useContext, useRef, useState } from "react";
import emailjs from "emailjs-com";
import { ThemeContext } from "../../context";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Contact = () => {
    const formRef = useRef();
    const [done, setDone] = useState(false)
    const theme = useContext(ThemeContext);
    const darkMode = theme.state.darkMode;

    const handleSubmit = (e) => {
      e.preventDefault();
      emailjs
        .sendForm(
          "service_okh08ja",
          "template_h9mncvd",
          formRef.current,
          "4s7XJ2FvMFcAt3vxe"
        )
        .then(
          (result) => {
            console.log(result.text);
            setDone(true)
          },
          (error) => {
            console.log(error.text);
          }
        );
        toast("ðŸŒ³ Merci pour votre message !", {
            position:"bottom-center",
            autoClose: 1500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
    };
    const updateDisplayForm = (e) => {
        setDone({
          ...done,
          [e.target.name]: e.target.value,
        });
      };

    return (
      <div className="c">
        <div className="c-bg"></div>
        <div className="c-wrapper">
          <div className="c-left">
            <h1 className="c-title">Let's discuss your project</h1>
            <div className="c-info">
              <div className="c-info-item">
                <img src={Phone} alt="" className="c-icon" />
                +1 1234 556 75
              </div>
              <div className="c-info-item">
                <img className="c-icon" src={Email} alt="" />
                contact@lama.dev
              </div>
              <div className="c-info-item">
                <img className="c-icon" src={Address} alt="" />
                245 King Street, Touterie Victoria 8520 Australia
              </div>
            </div>
          </div>
          <div className="c-right">
            <p className="c-desc">
              <b>Whatâ€™s your story?</b> Get in touch. Always available for
              freelancing if the right project comes along. me.
            </p>
            <form ref={formRef} onSubmit={handleSubmit}>
              <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Name" name="name" onChange={updateDisplayForm} />
              <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Subject" name="subject" onChange={updateDisplayForm}/>
              <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Email" name="email" onChange={updateDisplayForm} />
              <textarea style={{backgroundColor: darkMode && "#333"}} rows="5" placeholder="Message" name="message" onChange={updateDisplayForm} />
              <button className="c-sub">Submit</button>
            </form>
            <ToastContainer />
          </div>
        </div>
      </div>
    );
  };

  export default Contact;
